import { Injectable } from '@angular/core';
import { INumberEqualService } from '../INumberEqualService';
import { INumberBeetween3And5 } from "../../INumberBeetween3And5";
import { INumberIsOdd } from "../../ProductsModule/Core/Services.Abstractions/INumberIsOdd";
import { INumberIsEven } from "../../ProductsModule/Core/Services.Abstractions/INumberIsEven";
import { INumberEqualCondition } from "../INumberEqualCondition";

@Injectable()
export class NumberEqualService implements INumberEqualService{

  private _conditions: INumberEqualCondition[];

  constructor(private e1: INumberBeetween3And5, private e2: INumberIsOdd) { 
    this._conditions = [e1, e2];
  }

  public isEqual(number: number): boolean {
    
    for (const condition of this._conditions)
      if (condition.isEqual(number) == false)
        return false;
  
    return true;
  }
}
