import { Component } from '@angular/core';
import { IProductsRepository } from 'src/app/ProductsModule/Core/Domain/Repositories/IProductsRepository';
import { Product } from 'src/app/ProductsModule/Core/Domain/Entities/Product';
import { ICounter } from 'src/app/ProductsModule/Core/Services.Abstractions/ICounter';


@Component({
  selector: 'app-product-list-component',
  templateUrl: './product-list-component.component.html',
  styleUrls: ['./product-list-component.component.scss']
})
export class ProductListComponentComponent {

  constructor(private service: IProductsRepository, private _counter: ICounter) {}

  public getProducts(): Product[] {
    return this.service.getProducts();
  }

  share() {
    window.alert('The product has been shared!');
  }
}
