import { Injectable, InjectionToken } from "@angular/core";

@Injectable()
export abstract class INumberEqualService{
    abstract isEqual(number: number): boolean;
}

// @Injectable()
// export abstract class INumberEqualCondition 
// {
//   abstract isEqual(number: number): boolean;
// }


export abstract class INumberEqualCondition 
{
  abstract isEqual(number: number): boolean;
}

export abstract class INumberIsEven implements INumberEqualCondition { }

@Injectable()
export class NumberIsEven implements INumberIsEven
{
    public isEqual(number: number): boolean {
        return number % 2 == 0;
    }
}

@Injectable()
export class NumberIsOdd implements INumberEqualCondition
{
    public isEqual(number: number): boolean {
        return number % 2 != 0;
    }
}

@Injectable()
export class NumberBeetween3And5 implements INumberEqualCondition
{
    public isEqual(number: number): boolean {
        return number >= 3 && number <= 5;
    }
}

